        const signer = await ethers.getSigner("0x096760F208390250649E3e8763348E783AEF5562")

        usdcContract = new ethers.Contract(usdc, usdcABI, signer);
        await usdcContract.bridgeMint(owner.address, "100000000000000000000000");
        _2crvContract = new ethers.Contract(_2crv, _2crvABI, signer);
        gaugeContract = new ethers.Contract(gauge, gaugeABI, signer);
        _2poolContract = new ethers.Contract(_2pool, _2poolABI, signer);
